mapper.insert:
INSERT INTO PRODUCT
	(
	  PRODUCT_NO
	, PRODUCT_THUMBNAIL
	, PRODUCT_NAME
	, PRODUCT_EXPLANATION
	, PRODUCT_PRICE
	, PRODUCT_CATEGORY
	, PRODUCT_CONTEXT
	)
VALUES
	(
	  (SELECT NVL(MAX(PRODUCT_NO), 0)+1 FROM PRODUCT)
	, #{product_thumbNail, jdbcType=VARCHAR}
	, #{product_name}
	, #{product_explanation}
	, #{product_price}
	, #{product_category}
	, #{product_context}
	)

mapper.update:
update product
set imagepath=#{imagepath, jdbcType=VARCHAR}, name=#{name}, explanation=#{explanation}, price=#{price}, context=#{context},
	image1=#{image1, jdbcType=VARCHAR}, image2=#{image2, jdbcType=VARCHAR}, image3=#{image3, jdbcType=VARCHAR}
where no=#{no}
