mapper.select:
select no, imagepath, name, explanation, price, context, image1, image2, image3
from product
where no=#{no}

mapper.update:
update product
set imagepath=#{imagepath, jdbcType=VARCHAR}, name=#{name}, explanation=#{explanation}, price=#{price}, context=#{context},
	image1=#{image1, jdbcType=VARCHAR}, image2=#{image2, jdbcType=VARCHAR}, image3=#{image3, jdbcType=VARCHAR}
where no=#{no}
